class Solution:
    def validPalindrome(self, s):
        """
        :type s: str
        :rtype: bool
        """
        if s == s[::-1]: return True
        if s[1:] == s[1:][::-1]: return True
        if s[0:-1] == s[0:-1][::-1]: return True
        s_list = list(s)
        for i in range(len(s) - 1):
            list_after_delete_one = s_list[:i] + s_list[i+1:]
            if list_after_delete_one == list_after_delete_one[::-1]:
                return True
        return False

if __name__ == '__main__':
    solution = Solution()
    testdata = ""
    result = solution.validPalindrome(testdata)
    print(result)